import React, {useState, useEffect} from 'react'
import News from './News'
import InfiniteScroll from "react-infinite-scroll-component";


function NewsRow() {
    const [page, setPage] = useState(1);
    const [pageSize, setPageSize] = useState(15);
    const [country, setCountry] = useState('in');
    const [article, setArticle] = useState([]);
    const [loading, setLoading] = useState(true);

    const updateNews = async(page) => {
        const apiKEY = `https://newsapi.org/v2/top-headlines?country=${country}&apiKey=0b1bb2a2507c4856a05f094cc333c5f8&pagesize=${pageSize}&page=${page}`;
        let data = await fetch(apiKEY);
        let jsonData = await data.json();
        console.log(jsonData);
        setArticle(article.concat(jsonData.articles));
        setLoading(false);
    }

    useEffect(()=>{
        updateNews();
    }, [])

    const fetchMoreData = () => {
        setPage(page + 1);
        updateNews(page + 1);
    };

    return (
        <div className='container'>
            <InfiniteScroll dataLength={1000} next={fetchMoreData}>
            <div className="row my-3">
                {!loading && article.map((ele)=> {
                    return(<div className="col md-4"><News title={ele.title} desc={ele.description} imgUrl={ele.urlToImage} url={ele.url}/></div>)
                })}
            </div>
            </InfiniteScroll>
        </div>
    )
}

export default NewsRow
